import 'package:chat_app/themes/theme_provider.dart';import 'package:flutter/material.dart';import 'package:provider/provider.dart';class ChatDubble extends StatelessWidget {  final String message;  final bool isCurrentUser;  const ChatDubble({    super.key,    required this.message,    required this.isCurrentUser,  });  @override  Widget build(BuildContext context) {    /* light va dark mode for corrent bubble colors */    bool isDarkMode =        Provider.of<ThemeProvider>(context, listen: false).isDarkMode;    return Container(      margin: EdgeInsets.symmetric(vertical: 5, horizontal: 20),      padding: EdgeInsets.all(16),      decoration: BoxDecoration(        color: isCurrentUser            ? (isDarkMode ? Colors.green.shade600 : Colors.grey.shade500)            : (isDarkMode ? Colors.grey.shade800 : Colors.green.shade200),        borderRadius: BorderRadius.circular(10),      ),      child: Text(        message,        style: TextStyle(          color: isCurrentUser              ? Colors.white              : (isDarkMode ? Colors.white : Colors.black),        ),      ),    );  }}