import 'package:chat_app/pages/setting_page.dart';import 'package:chat_app/services/auth/auth_service.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';class MyDrawer extends StatelessWidget {  const MyDrawer({super.key});  /* logout*/  void logout() {    /* get auth service */    final _auth = AuthService();    _auth.signOut();  }  @override  Widget build(BuildContext context) {    return Drawer(      backgroundColor: Theme.of(context).colorScheme.background,      child: Column(        mainAxisAlignment: MainAxisAlignment.spaceBetween,        children: <Widget>[          Column(            children: <Widget>[              /* Logo */              DrawerHeader(                decoration: BoxDecoration(                  border: null,                ),                child: Center(                  child: Icon(                    Icons.message,                    color: Theme.of(context).colorScheme.primary,                    size: 40,                  ),                ),              ),              /* Home List Tile */              Padding(                padding: const EdgeInsets.only(left: 25),                child: ListTile(                  leading: Icon(CupertinoIcons.home),                  title: Text(                    'H O M E',                    style: TextStyle(fontFamily: 'plusR'),                  ),                  onTap: () {                    // Pop the Drawer                    Navigator.pop(context);                  },                ),              ),              /* Settings List Tile */              Padding(                padding: const EdgeInsets.only(left: 25),                child: ListTile(                  leading: Icon(CupertinoIcons.settings),                  title: Text(                    'S E T T I N G',                    style: TextStyle(fontFamily: 'plusR'),                  ),                  onTap: () {                    /*Pop the Drawer*/                    Navigator.pop(context);                    Navigator.push(                      context,                      MaterialPageRoute(                        builder: (context) => SettingPage(),                      ),                    );                  },                ),              ),            ],          ),          /* LogOut List Tile */          Padding(            padding: const EdgeInsets.only(left: 25),            child: ListTile(              leading: Icon(Icons.login_rounded),              title: Text(                'L O G O U T',                style: TextStyle(fontFamily: 'plusR'),              ),              onTap: logout,            ),          ),        ],      ),    );  }}