import 'package:chat_app/componets/my_button.dart';import 'package:chat_app/componets/my_textfield.dart';import 'package:chat_app/services/auth/auth_service.dart';import 'package:flutter/material.dart';class RegisterPage extends StatelessWidget {  // tap to go to register  final void Function()? onTap;  const RegisterPage({super.key, required this.onTap});  @override  Widget build(BuildContext context) {    TextEditingController _emailController = TextEditingController();    TextEditingController _passwordController = TextEditingController();    TextEditingController _confirmController = TextEditingController();    // Login Method    void register(BuildContext context) {      /* get auth service */      final _auth = AuthService();      /* passwords match --> create user  */      if (_passwordController.text == _confirmController.text) {        try {          _auth.signUpWithEmailPassword(            _emailController.text,            _passwordController.text,          );        } catch (e) {          showDialog(            context: context,            builder: (context) => AlertDialog(              title: Text(e.toString()),            ),          );        }      }      /* passwords dont match --> tell user to fix  */      else {        showDialog(          context: context,          builder: (context) => AlertDialog(            title: Text("Passwords don't match!"),          ),        );      }    }    return Scaffold(      backgroundColor: Theme.of(context).colorScheme.background,      body: SafeArea(        child: Center(          child: Column(            mainAxisAlignment: MainAxisAlignment.center,            children: <Widget>[              /* Logo */              Icon(                Icons.message,                size: 60,                color: Theme.of(context).colorScheme.primary,              ),              SizedBox(height: 45),              /* WelCome Back */              Text(                "Let's create new account for you",                style: TextStyle(                  color: Theme.of(context).colorScheme.primary,                  fontSize: 16,                ),              ),              SizedBox(height: 25),              /* email textfield */              MyTextField(                controller: _emailController,                obscureText: false,                hintText: 'Email',              ),              MyTextField(                controller: _passwordController,                obscureText: true,                hintText: 'Password',              ),              MyTextField(                controller: _confirmController,                obscureText: true,                hintText: 'Confirm password',              ),              /* Button */              MyButton(                text: "Register",                onTap: () => register(context),              ),              SizedBox(height: 25),              /* register now */              Row(                mainAxisAlignment: MainAxisAlignment.center,                children: <Widget>[                  Text(                    "Already have an account?",                    style: TextStyle(                        color: Theme.of(context).colorScheme.primary),                  ),                  SizedBox(width: 5),                  GestureDetector(                    onTap: onTap,                    child: Text(                      'Login Now',                      style: TextStyle(                        color: Theme.of(context).colorScheme.primary,                        fontWeight: FontWeight.w500,                        fontSize: 16,                      ),                    ),                  ),                ],              ),            ],          ),        ),      ),    );  }}