import 'package:chat_app/themes/theme_provider.dart';import 'package:flutter/cupertino.dart';import 'package:flutter/material.dart';import 'package:provider/provider.dart';class SettingPage extends StatefulWidget {  const SettingPage({super.key});  @override  State<SettingPage> createState() => _SettingPageState();}class _SettingPageState extends State<SettingPage> {  @override  Widget build(BuildContext context) {    bool switchValue = false;    return Scaffold(      backgroundColor: Theme.of(context).colorScheme.background,      appBar: AppBar(        centerTitle: true,        title: Text('S E E T I N G'),        backgroundColor: Colors.transparent,        foregroundColor: Colors.grey,        elevation: 0,      ),      body: Column(        children: [          Container(            decoration: BoxDecoration(              color: Theme.of(context).colorScheme.secondary,              borderRadius: BorderRadius.circular(12),            ),            margin: EdgeInsets.all(25),            padding: EdgeInsets.all(16),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: <Widget>[                /* dark mode */                Text("Dark Mode"),                /* switch toggle */                CupertinoSwitch(                  value: Provider.of<ThemeProvider>(context, listen: false)                      .isDarkMode,                  onChanged: (value) =>                      Provider.of<ThemeProvider>(context, listen: false)                          .toggleTheme(),                ),              ],            ),          ),          Container(            decoration: BoxDecoration(              color: Theme.of(context).colorScheme.secondary,              borderRadius: BorderRadius.circular(12),            ),            margin: EdgeInsets.all(25),            padding: EdgeInsets.all(16),            child: Row(              mainAxisAlignment: MainAxisAlignment.spaceBetween,              children: <Widget>[                /* dark mode */                Text("Get Notification"),                /* switch toggle */                CupertinoSwitch(                  // This bool value toggles the switch.                  value: switchValue,                  activeColor: CupertinoColors.activeGreen,                  onChanged: (bool? value) {                    // This is called when the user toggles the switch.                    setState(                      () {                        switchValue = value ?? false;                      },                    );                  },                ),              ],            ),          ),        ],      ),    );  }}